import React, { useState } from 'react';
import ProductListPage from './ProductListPage';
import ProductCreationPage from './ProductCreationPage';
import ProductDetailPage from './ProductDetailPage';
import ProductDetail from './Product/ProductDetail';

// Dummy Data
const dummyCars = [
    { id: 1, title: 'Tesla Model S', imageUrl: 'https://via.placeholder.com/400x250', description: 'Electric sedan with autopilot.' },
    { id: 2, title: 'Ford Mustang', imageUrl: 'https://via.placeholder.com/400x250', description: 'Classic muscle car with a powerful engine.' },
    { id: 3, title: 'Toyota Supra', imageUrl: 'https://via.placeholder.com/400x250', description: 'Sports car known for its speed.' },
];

const App = () => {
    const [cars, setCars] = useState(dummyCars);
    const [selectedCar, setSelectedCar] = useState(null);

    const addCar = (newCar) => setCars([...cars, newCar]);
    const deleteCar = (id) => setCars(cars.filter(car => car.id !== id));
    const editCar = (updatedCar) => setCars(cars.map(car => car.id === updatedCar.id ? updatedCar : car));

    return (
        <div>
            {/* You can conditionally render pages based on user interaction */}
            {selectedCar ? (
                <ProductDetail car={selectedCar} deleteCar={deleteCar} editCar={editCar} />
            ) : (
                <>
                    <ProductListPage cars={cars} selectCar={setSelectedCar} />
                    <ProductCreationPage addCar={addCar} />
                </>
            )}
        </div>
    );
};

export default App;
