import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate, BrowserRouter } from 'react-router-dom';
import ProductList from './Product/ProductList';
import ProductCreation from './Product/ProductCreation';
import ProductDetail from './Product/ProductDetail';
import Login from './Register/Login';
import Register from './Register/Register';

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false); // State for checking if the user is authenticated
    // const navigate = useNavigate(); // Using useNavigate inside a component that is wrapped in <Router>

    // // Handle login (in a real app, you would authenticate here)
    // const handleLogin = () => {
    //     setIsAuthenticated(true);
    //     navigate('/home'); // Redirect to the main page after login
    // };

    return (
        <BrowserRouter>
            <Router>
                <div>
                    {/* Navbar */}
                    <nav>
                        {isAuthenticated ? (
                            <>
                                <Link to="/home" style={{ margin: '10px' }}>Home</Link>
                                <Link to="/create" style={{ margin: '10px' }}>Create Car</Link>
                            </>
                        ) : (
                            <>
                                <Link to="/login" style={{ margin: '10px' }}>Login</Link>
                                <Link to="/register" style={{ margin: '10px' }}>Register</Link>
                            </>
                        )}
                    </nav>

                    <Routes>
                        {/* Authentication Routes */}
                        <Route path="/login" element={<Login onLogin={handleLogin} />} />
                        <Route path="/register" element={<Register />} />

                        {/* Main Routes */}
                        {isAuthenticated ? (
                            <>
                                <Route path="/home" element={<ProductList />} />
                                <Route path="/create" element={<ProductCreation />} />
                                <Route path="/product/:id" element={<ProductDetail />} />
                            </>
                        ) : (
                            <Route path="*" element={<Login />} /> // Redirect unauthenticated users
                        )}
                    </Routes>
                </div>
            </Router>
        </BrowserRouter>
    );
};

export default App;
