import React, { useState } from 'react';

const ProductCreationPage = () => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);
    const [cars, setCars] = useState([
        { id: 1, title: 'Tesla Model S', imageUrl: 'https://via.placeholder.com/400x250', description: 'Electric sedan with autopilot.' },
        { id: 2, title: 'Ford Mustang', imageUrl: 'https://via.placeholder.com/400x250', description: 'Classic muscle car with a powerful engine.' },
        { id: 3, title: 'Toyota Supra', imageUrl: 'https://via.placeholder.com/400x250', description: 'Sports car known for its speed.' },
    ]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const newCar = { id: Date.now(), title, description, imageUrl: image };
        setCars([...cars, newCar]);
        setTitle('');
        setDescription('');
        setImage(null);
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImage(URL.createObjectURL(file));
        }
    };

    return (
        <div className="container mx-auto py-10 px-6 max-w-lg">
            <h1 className="text-3xl font-bold mb-8 text-gray-800 text-center">Create New Car</h1>
            <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-lg">
                <div className="mb-6">
                    <label className="block text-gray-700 font-semibold mb-2">Upload Image</label>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleImageUpload}
                        className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                    />
                    {image && (
                        <img src={image} alt="Preview" className="mt-4 w-full h-48 object-cover rounded-lg" />
                    )}
                </div>
                <div className="mb-6">
                    <label className="block text-gray-700 font-semibold mb-2">Car Title</label>
                    <input
                        type="text"
                        placeholder="Enter car title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        className="w-full p-3 border border-gray-300 rounded-lg"
                    />
                </div>
                <div className="mb-6">
                    <label className="block text-gray-700 font-semibold mb-2">Description</label>
                    <textarea
                        placeholder="Write a description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        className="w-full p-3 border border-gray-300 rounded-lg"
                        rows="4"
                    ></textarea>
                </div>
                <button
                    type="submit"
                    className="w-full bg-blue-500 text-white py-3 rounded-lg font-semibold hover:bg-blue-600"
                >
                    Create Car
                </button>
            </form>
        </div>
    );
};

export default ProductCreationPage;
