import React, { useEffect, useState } from 'react';
import { Card, Col, Row, Form, Container, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProductList = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [cars, setCars] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCars = async () => {
            try {
                const response = await axios.get('/api/cars'); // Adjust the endpoint if necessary
                setCars(response.data);
            } catch (error) {
                console.error('Error fetching cars:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchCars();
    }, []);

    if (loading) return <p>Loading cars...</p>;



    // const navigate = useNavigate();
    // const filteredCars = cars.filter(car => car.title.toLowerCase().includes(searchTerm.toLowerCase()));

    // // Function to handle navigation to product detail page
    // const viewDetails = (carId) => {
    //     navigate(`/product/${carId}`);
    // };

    return (
        <Container className="py-5">
            <h1 className="text-center mb-4">My Cars</h1>

            {/* Search Input */}
            <div className="mb-4">
                <Form.Control
                    type="text"
                    placeholder="Search cars..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="shadow-sm"
                />
            </div>

            {/* Cars List */}
            <Row xs={1} md={2} lg={3} className="g-4">
                {filteredCars.map((car) => (
                    <Col key={car.id}>
                        <Card className="shadow-sm rounded-lg">
                            {/* Image with a fixed height */}
                            <Card.Img
                                variant="top"
                                src={car.imageUrl}
                                alt={car.title}
                                className="img-fluid"
                                style={{ height: '200px', objectFit: 'cover' }}
                            />
                            <Card.Body>
                                {/* Title: Truncate if it's too long */}
                                <Card.Title style={{ whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                                    {car.title}
                                </Card.Title>

                                {/* Description: Add "Read More" feature */}
                                <Card.Text>
                                    {car.description.length > 100
                                        ? `${car.description.substring(0, 100)}...`
                                        : car.description
                                    }
                                    {car.description.length > 100 && (
                                        <span className="text-primary" style={{ cursor: 'pointer' }} onClick={() => alert('Show full description')}>
                                            Read more
                                        </span>
                                    )}
                                </Card.Text>

                                {/* View Detail Button */}
                                <Button
                                    variant="primary"
                                    onClick={() => viewDetails(car.id)}
                                    className="w-100">
                                    View Details
                                </Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    );
};

export default ProductList;
